function doSearch(b,a){if(null==a){a=""}$.ajax({type:"POST",url:b,data:a,dataType:"text",success:function(c){$("#listArea #content").empty().append(c);$("#listArea #content").show()},error:function(c,e,d){alert(e+":"+d)}})}function doSelectAll(a){$("#frmList :checkbox[name='bkgMasterId']").attr("checked",a.checked)}function gotoList(){$("#viewArea").hide();$("#listArea").show();jumpToPosition("listArea_start")}function doNewBkgMaster(){doViewBooking("")}function doViewBooking(b,c){var a;if(c!=""){a="action=edit&bkgMasterId="+c}$.ajax({type:"POST",url:b,data:a,dataType:"text",success:function(d){$("#viewBooking #content").empty().append(d);$("#viewBooking #title #bookingId").empty().append(c);$("#listArea").hide();$("#editBooking").hide();$("#viewBooking").show();$("#viewArea").show()},error:function(d,f,e){alert(f+"|"+e)}})}function doCancelEdit(){$("#listArea").css("display","");$("#editArea").css("display","none")}function doAction(a){alert(a.value);a.options[0].selected=true}function doEnable(a){alert(a)}function doChangeClass(b,a){b.className=a}function doFocus(b,a){if(b.value==a){b.value="";b.style.color=""}}function doBlur(b,a){if(b.value==""){b.value=a;b.style.color="#EEEEEE"}}function doDeleteBkgMaster(){alert("doDeleteBkgMaster")}function doCancel(){$("#editBooking").hide();$("#viewBooking").show()}function activeTab(a){$("#tab_bkgMaster").removeClass("active");$("#tab_bkgCargo").removeClass("active");$("#tab_bkgMarkNo").removeClass("active");$("#tab_bkgCtrOfBkg").removeClass("active");$("#tab_bkgContainer").removeClass("active");$("#tab_bkgNote").removeClass("active");$("#tab_bkgStatus").removeClass("active");window.location="#"+a}function jumpToPosition(a){window.location="#"+a}function doEdit(a){$("#viewBooking").hide();$("#editBooking p").empty().append("Edit "+a);$("#editBooking").show()}function doEditBkgMaster(c){var b="<%=contextPath%>/booking/bookingAdmin/editBkgMaster.do";var a="action=create&bkgMasterId=";if(c!=""){a="action=edit&bkgMasterId="+c}$.ajax({type:"POST",url:b,data:a,dataType:"text",success:function(d){$("#editBooking #title #bookingId").empty().append(c);$("#editBooking #title #editTitle").empty().append("Edit Master");$("#editBooking #content").empty().append(d);$("#viewBooking").hide();$("#editBooking").show()},error:function(d,f,e){alert(f+"|"+e)}})}function saveBkgMaster(){var b="<%=contextPath%>/booking/bookingAdmin/saveBkgMaster.do";var a=$("#frmBkgMaster").serialize();$.ajax({type:"POST",url:b,data:a,dataType:"text",success:function(c){$("#editBooking").hide();$("#viewBooking").show()},error:function(c,e,d){alert(e)}})}function doEditBkgCargo(c,d){var b="<%=contextPath%>/booking/bookingAdmin/editBkgCargo.do";var a="action=create&bkgCargoId=";if(c!=""){a="action=edit&bkgCargoId="+c+"&bkgMasterId"+d}$.ajax({type:"POST",url:b,data:a,dataType:"text",success:function(e){$("#editBooking #title #bookingId").empty().append(d);$("#editBooking #title #editTitle").empty().append("Edit Cargo");$("#editBooking #content").empty().append(e);$("#viewBooking").hide();$("#editBooking").show()},error:function(e,g,f){alert(g+"|"+f)}})}function saveBkgCargo(){var b="<%=contextPath%>/booking/bookingAdmin/saveBkgCargo.do";var a=$("#frmBkgCargo").serialize();$.ajax({type:"POST",url:b,data:a,dataType:"text",success:function(c){$("#editBooking").hide();$("#viewBooking").show()},error:function(c,e,d){alert(e)}})};